import { Vector3 } from "three";
import * as THREE from "three";
// import { Sphere } from "./Sphere";
import { Sphere } from "three";

export class World {
    private objects: any[] = [];
    private currentLoopInterval: any = null;

    constructor() {
        this.objects = [];
    }

    startGameLoop(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 3);
        const s2 = new Sphere(new Vector3(5, 0, 0), 3);
        const s3 = new Sphere(new Vector3(0, 5, 0), 3);

        this.objects = [];
        this.objects.push(s);
        this.objects.push(s2);
        this.objects.push(s3);

        this.clearCurrentLoop();

        let i = 0;
        // this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);

        function animate(): void {
            requestAnimationFrame(animate);
            console.log('hi');
        }
        // animate();
    }

    runLoop(): void {
        let i = 0;
        console.log("clearing: ", this.currentLoopInterval);
        clearInterval(this.currentLoopInterval);
        this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);
    }

    clearCurrentLoop(): void {
        clearInterval(this.currentLoopInterval);
    }

    test(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 1);
        this.objects.push(s);
        console.log("hi");
        console.log(this.objects);
    }
}
