import { Mesh, Vector3 } from "three";
import * as THREE from "three";

export class Plane {
    public point: Vector3;
    public normal: Vector3;

    // for graphics
    public mesh: Mesh;

    constructor(point: Vector3, normal: Vector3) {
        this.point = point;
        this.normal = normal;
    }

    private createMesh(): void {
        const planeGeometry = new THREE.PlaneGeometry(1000, 1000);
        const material = new THREE.MeshPhongMaterial({ color: 0xffcb3e });
        this.mesh = new THREE.Mesh(planeGeometry, material);
        this.mesh.position.set(this.point.x, this.point.y, this.point.z);
        // todo: set orientation based on normal
    }

    integrate(duration: number): void {
        // do nothing for now, since plane doesn't move.
    }
}
