import * as THREE from "three";
import { Vector3, Matrix3, Quaternion } from "three";
import { getVectorCrossMatrix, addMatrices, convertQuaternionToMatrix, addQuaternions, scaleQuaternion, getEulerAngles } from "./math_library";

export class Box {
    public inverseMass: number;

    public width: number;
    public length: number;
    public height: number;

    public inertia: Matrix3; // inertia tensor in body space

    public position: Vector3;
    public momentum: Vector3;

    public rotation: Quaternion // the orientation
    //public rotation: Matrix3; // the orientation
    public angularMomentum: Vector3;

    constructor(position: Vector3, width: number, length: number, height: number) {
        this.inverseMass = 1 / (width * length * height);

        this.width = width;
        this.length = length;
        this.height = height;

        this.inertia = new Matrix3();
        this.setInertia();

        this.position = position;
        this.momentum = (new Vector3(1, 0, 0)).multiplyScalar(1/this.inverseMass);

        this.rotation = new Quaternion(0, 0, 0, 1);
        //this.rotation = new Matrix3();
        this.angularMomentum = new Vector3(0, 0, 1).applyMatrix3(this.inertia);
        console.log("starting angularMomentum", this.angularMomentum);
    }

    private setInertia(): void {
        const I_xx = (1/12) * (this.length*this.length + this.height*this.height);
        const I_yy = (1/12) * (this.width*this.width + this.height*this.height);
        const I_zz = (1/12) * (this.width*this.width + this.length*this.length);
        const I_xy = (-1/16) * (this.width*this.length);
        const I_yx = I_xy;
        const I_xz = (-1/16) * (this.width*this.height);
        const I_zx = I_xz;
        const I_yz = (-1/16) * (this.length*this.height);
        const I_zy = I_yz;

        this.inertia.set(
            I_xx, I_xy, I_xz,
            I_yx, I_yy, I_yz,
            I_zx, I_zy, I_zz
        );
    }

    integrate(duration: number): void {
        // Don't integrate if mass is infinite or duration <= 0.
        if (this.inverseMass <= 0 || duration <= 0) {
            return;
        }

        this.position.addScaledVector(this.momentum, this.inverseMass * duration);

        // update rotation matrix
    }

    testIntegrate(duration: number): void {
        const I_b_inverse = this.inertia.clone().invert();
        const rotationMatrix = convertQuaternionToMatrix(this.rotation);
        const I_inverse = rotationMatrix.clone().multiply(I_b_inverse).multiply(rotationMatrix.clone().transpose());
        const angularVelocity = this.angularMomentum.clone().applyMatrix3(I_inverse);

        const testAngularVelocity = new Vector3(0, 0, 1);

        // ddt_q = (1/2) * w(t) * q(t)
        const ddt_Rotation = (new Quaternion(testAngularVelocity.x/2, testAngularVelocity.y/2, testAngularVelocity.z/2, 0)).multiply(this.rotation);
        this.rotation = addQuaternions(this.rotation, scaleQuaternion(ddt_Rotation, duration));
        this.rotation.normalize();

        //console.log("angular momentum: ", this.angularMomentum);
        //console.log("angularVelocity: ", angularVelocity);
        //const newRotationMatrix = convertQuaternionToMatrix(this.rotation);
        //const eulerAngles = getEulerAngles(newRotationMatrix);
        //console.log("psi: ", eulerAngles.psi, " theta: ", eulerAngles.theta, " phi: ", eulerAngles.phi);
    }

