import { Vector3 } from "three";
import * as THREE from "three";
import { Sphere } from "./Sphere";
import { createRenderer } from "react-dom/test-utils";

const CANVAS_WIDTH = 1000;
const CANVAS_HEIGHT = 600;

export class World {
    private objects: any[] = [];
    private currentLoopInterval: any = null;

    constructor(private canvas: HTMLCanvasElement) {
        this.objects = [];
    }

    startGameLoop(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 1);

        this.clearCurrentLoop();

        let i = 0;
        // this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, CANVAS_WIDTH / CANVAS_HEIGHT, 0.1, 1000);

        console.log("this.canvas", this.canvas);
        const renderer = new THREE.WebGLRenderer({ canvas: this.canvas, alpha: true });
        renderer.setSize(CANVAS_HEIGHT, CANVAS_WIDTH);

        const sphereGeometry = new THREE.SphereGeometry(1);
        const material = new THREE.MeshBasicMaterial({color: 0xffff00});
        const sphere = new THREE.Mesh(sphereGeometry, material);

        scene.add(sphere);

        function animate(): void {
            requestAnimationFrame(animate);
            console.log('hi');
            renderer.render(scene, camera);
        }
        animate();
    }

    runLoop(): void {
        let i = 0;
        console.log("clearing: ", this.currentLoopInterval);
        clearInterval(this.currentLoopInterval);
        this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);
    }

    clearCurrentLoop(): void {
        clearInterval(this.currentLoopInterval);
    }

    test(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 1);
        this.objects.push(s);
        console.log("hi");
        console.log(this.objects);
    }
}
