    const vector_ab = a.position.clone().sub(b.position);
    const a_facePlanes = getFacePlanes(a);
    let smallestOverlap = Infinity;
    let contact;
    for (let facePlane of a_facePlanes) {
        const overlap = separatingAxisPenetration(a, b, facePlane.normal);
