        }
    }
    const b_facePlanes = getFacePlanes(b);
    for (let facePlane of b_facePlanes) {
        const overlap = separatingAxisPenetration(a, b, facePlane.normal);
        if (overlap < smallestOverlap) {
            smallestOverlap = overlap;

            // set contact
            // set contact
            let axis = facePlane.normal.clone();
            if (vector_ab.clone().dot(axis) < 0) { // set axis to normal of the correct face.
                axis.multiplyScalar(-1);
