import React from "react";
import { World } from "../physics_engine/physics_engine";
import * as THREE from "three";
import { Matrix3, Scene, Vector3 } from "three";
import { Sphere } from "../physics_engine/Sphere";
import { Box } from "../physics_engine/Box";
import { OrbitControls } from "three-orbitcontrols-ts";
import { getEulerAngles, xRotationMatrix3, yRotationMatrix3, zRotationMatrix3, convertMatrix3ToMatrix4 } from "../physics_engine/math_library";

const CANVAS_WIDTH = 1000;
const CANVAS_HEIGHT = 500;

// camera
const camera = new THREE.PerspectiveCamera(45, CANVAS_WIDTH / CANVAS_HEIGHT, 1, 1000);
camera.position.set(0, 0, CANVAS_HEIGHT / 4);
camera.up.set(0, 1, 0);

// add lighting
const light_color = 0xffffff;
const intensity = 1;
const point_light = new THREE.PointLight(light_color, intensity);
point_light.position.set(10, 10, 10);

const ambient_light = new THREE.AmbientLight(light_color, 0.2);

export default class Playground3 extends React.Component {
    private canvasRef: any;
    private renderer: any;
    private animationRequestID: any;

    constructor(props: any) {
        super(props);
        this.canvasRef = React.createRef();

        // graphics
        this.renderer = null;
        this.animationRequestID = null;
    }

    componentDidMount(): void {
        // graphics
        this.renderer = new THREE.WebGLRenderer({ canvas: this.canvasRef.current, alpha: false })
        this.renderer.setSize(CANVAS_WIDTH, CANVAS_HEIGHT);

        // orbit controls
        const controls = new OrbitControls(camera, this.renderer.domElement);

        this.setUpSpheres();
        this.onVisualize();
    }

    setUpSpheres(): void {
        const s = new Sphere(new Vector3(-50, 0, 0), 5);
        const s2 = new Sphere(new Vector3(0, 5, 0), 5);

        s.velocity = new Vector3(10, 0, 0);
    }

    onVisualize() {
        // cancel ongoing animation
        if (this.animationRequestID) {
            cancelAnimationFrame(this.animationRequestID);
        }

        // set up objects
        const b = new Box(new Vector3(0, 0, 20), 10, 10, 10);

        const boxGeometry = new THREE.BoxGeometry(b.width, b.length, b.height);
        const material = new THREE.MeshPhongMaterial({ color: 0x00bcd6 });

        const box = new THREE.Mesh(boxGeometry, material);

        // set up scene
        const scene = new THREE.Scene();
        scene.add(box);

        // add lighting
        scene.add(point_light, ambient_light);

        // add gridHelper
        const gridHelper = new THREE.GridHelper( 100, 10 );
        gridHelper.rotateOnWorldAxis(new Vector3(1, 0, 0), Math.PI / 2);
        scene.add(gridHelper);

        let xRotationAngle = 0;
        let yRotationAngle = 0;
        let zRotationAngle = 0;

        const currentInstance = this;
        function animate() {
            currentInstance.animationRequestID = requestAnimationFrame(animate);

            box.position.set(b.position.x, b.position.y, b.position.z);

            //xRotationAngle += 0.01;
            //yRotationAngle += 0.01;
            zRotationAngle += 0.01;

            // set rotation of physical box
            b.rotation = new Matrix3();
            b.rotation.premultiply(xRotationMatrix3(xRotationAngle));
            b.rotation.premultiply(yRotationMatrix3(yRotationAngle));
            b.rotation.premultiply(zRotationMatrix3(zRotationAngle));

            b.testIntegrate(1);

            // update rotation of the box mesh
            box.rotation.setFromRotationMatrix(convertMatrix3ToMatrix4(b.rotation));
            /* const eulerAngles = getEulerAngles(b.rotation);
            box.rotation.x = eulerAngles.psi;
            box.rotation.y = eulerAngles.theta;
            box.rotation.z = eulerAngles.phi;*/

            currentInstance.renderer.render(scene, camera);
        }

        animate();
    }

    render() {
        return (
            <div className="playground">
                <h2>Playground 3 page desu...</h2>
                <div id="canvas_container">
                    <canvas ref={this.canvasRef} width={CANVAS_WIDTH} height={CANVAS_HEIGHT} />
                </div>
            </div>
        )
    }
}
