import { Vector3 } from "three";
import * as THREE from "three";
import { Sphere } from "./Sphere";

export class World {
    private objects: any[] = [];
    private currentLoopInterval = null;

    constructor() {
        this.objects = [];
    }

    runLoop(): void {
        let i = 0;
        clearInterval(this.currentLoopInterval? this.currentLoopInterval : 0);
        const test = setInterval(() => {i++; console.log(i)}, 1000);
        console.log("test:", test);
        // this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);
    }

    test(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 1);
        this.objects.push(s);
        console.log("hi");
        console.log(this.objects);
    }
}
