import { Vector3 } from "three";
import * as THREE from "three";
import { Sphere } from "./Sphere";
// import { Sphere } from "three";

const dt = 1/60; // time step in seconds

export class World {
    private objects: any[] = [];
    private currentLoopInterval: any = null;

    private characterForce: Vector3;

    constructor() {
        this.objects = [];

        this.characterForce = new Vector3(0, 0, 0);
    }

    startLoop(): void {
        const currentInstance = this;

        // stop current loop if it exits
        this.stopCurrentLoop();


        this.currentLoopInterval = setInterval(animate, dt * 1000);
    }

    /** Stops current loop if it exists. */
    stopCurrentLoop(): void {
        if (this.currentLoopInterval) {
            clearInterval(this.currentLoopInterval);
        }
    }

    startGameLoop(): void {
        const s = new Sphere(new Vector3(0, 0, 0), 3);
        const s2 = new Sphere(new Vector3(10, 0, 0), 3);
        const s3 = new Sphere(new Vector3(0, 5, 0), 3);

        this.objects = [];
        this.objects.push(s);
        this.objects.push(s2);
        //this.objects.push(s3);

        this.clearCurrentLoop();

        let lastCalledTime = performance.now() // returns timestamp in ms
        let fps = 0;
        let times = [];

        let i = 0;
        // this.currentLoopInterval = setInterval(() => {i++; console.log(i)}, 1000);

        const currentInstance = this;
        function animate(): void {
            requestAnimationFrame(animate);

            s.forceAccum.add(currentInstance.characterForce);
            currentInstance.characterForce = new Vector3(0, 0, 0);

            s.integrate(1/60);
        }
        animate();
    }

    getObjects(): any[] {
        return this.objects;
    }

    updateCharacterForce(f: Vector3) {
        this.characterForce = f;
    }

    detectCollisions(): void {

    }

    checkCollisionSpheres(s1: Sphere, s2: Sphere) {
        const d = (s1.position.clone().sub(s2.position)).length(); // distance between centers

        // collision if distance between spheres is smaller than sub of radiuses
        if (d < (s1.radius + s2.radius)) {
            return true;
        }
        return false;
    }

    clearCurrentLoop(): void {
        clearInterval(this.currentLoopInterval);
    }
}
